package Interview;

public class evenoddnumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
       for(int i=0;i<=100;i++) {
    	   if(i%2==0) {
    		   System.out.println(i+"it is a even number");
    	   }
    	   else {
    		   System.out.println(i+"it is a odd number");
    	   }
       }
	}

}
